import "../mystyles.css";

const buttonTextArray = [
  "No?",
  "Really?",
  "Really really?",
  "Please",
  "Why do you insist on saying no...",
  "Don't do this to me ;n;",
  "I'm going to cry",
  "My little heart ;n;",
  "Please",
  "PleasePleasePleasePleasePlease",
  "PleasePleasePleasePleasePleasePleasePlease",
  "Reconsider, let's try again",
  "No",
];

