import React, { useState } from "react";
import "../mystyles.css";

const buttonTextArray = [
  "No?",
  "Really?",
  "Really really?",
  "Please",
  "Why do you insist on saying no...",
  "Don't do this to me ;n;",
  "I'm going to cry",
  "My little heart ;n;",
  "Please",
  "PleasePleasePleasePleasePlease",
  "PleasePleasePleasePleasePleasePleasePlease",
  "Reconsider, let's try again",
  "No",
];

const VDayQuestion = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [fontSize, setFontSize] = useState(16);
  const [yesClicked, setYesClicked] = useState(false);

  const updateButtonText = () => {
    setCurrentIndex((currentIndex + 1) % buttonTextArray.length);
  };

  const increaseFontSize = () => {
    setFontSize(fontSize + 20);
  };

  const onYesClick = () => {
    setYesClicked(true); 
  };

  return (
    <div>
      <h3>Will you be my Valentine? &lt;3</h3>
      <div className="center">
        <img
          src="https://media1.tenor.com/m/vpxL0d_5_98AAAAC/love-heart.gif"
          alt="Love Heart"
          style={{ width: "30%", height: "auto" }}
        />
      </div>
      <div className="button-container">
        <button className="button-primary" id="yesButton">
          Yes
        </button>
        <button
          className="button-secondary"
          id="dynamicButton"
          onClick={() => {
            updateButtonText();
            increaseFontSize();
          }}
          style={{ fontSize }}
        >
          {buttonTextArray[currentIndex]}
        </button>
      </div>
    </div>
  );
};

export default VDayQuestion;
